/**
 * A handler that updates the text of a BPMN element.
 *
 */
export default class UpdateLabelHandler {
    static $inject: string[];
    /**
     * @param modeling
     * @param textRenderer
     * @param bpmnFactory
     */
    constructor(modeling: Modeling, textRenderer: TextRenderer, bpmnFactory: BpmnFactory);
    preExecute: (ctx: any) => void;
    execute: (ctx: any) => any[];
    revert: (ctx: any) => any[];
    postExecute: (ctx: any) => void;
}

type Modeling = import('bpmn-js/lib/features/modeling/Modeling').default;
type TextRenderer = import('bpmn-js/lib/draw/TextRenderer').default;
type BpmnFactory = import('bpmn-js/lib/features/modeling/BpmnFactory').default;
type Element = import('bpmn-js/lib/model/Types').Element;
