/**
 * BPMN-specific replace.
 *
 */
export default class BpmnReplace {
    static $inject: string[];
    /**
     * @param bpmnFactory
     * @param elementFactory
     * @param moddleCopy
     * @param modeling
     * @param replace
     * @param rules
     */
    constructor(bpmnFactory: BpmnFactory, elementFactory: ElementFactory, moddleCopy: ModdleCopy, modeling: Modeling, replace: Replace, rules: Rules);
    replaceElement: (element: Element, targetElement: TargetElement, hints?: Hints) => Element;
}

type BpmnFactory = import('bpmn-js/lib/features/modeling/BpmnFactory').default;
type ElementFactory = import('bpmn-js/lib/features/modeling/ElementFactory').default;
type ModdleCopy = import('bpmn-js/lib/features/copy-paste/ModdleCopy').default;
type Modeling = import('bpmn-js/lib/features/modeling/Modeling').default;
type Replace = import('diagram-js/lib/features/replace/Replace').default;
type Rules = import('diagram-js/lib/features/rules/Rules').default;
type Element = import('bpmn-js/lib/model/Types').Element;
type Shape = import('bpmn-js/lib/model/Types').Shape;
type ModdleElement = import('bpmn-js/lib/model/Types').ModdleElement;

export type TargetElement = {
    type: string;
    cancelActivity: boolean;
    instantiate: boolean;
    eventGatewayType: string;
    triggeredByEvent: boolean;
    isInterrupting: boolean;
    collapsed: boolean;
    isExpanded: boolean;
    eventDefinitionType: string;
    eventDefinitionAttrs: any;
    host: Shape;
};

export type Hints = {
    moveChildren: boolean;
} & Record<string, any>;

import { isExpanded } from 'bpmn-js/lib/util/DiUtil';
