export default class AlignElements {
  static $inject: string[];

  /**
   * @param modeling
   * @param rules
   */
  constructor(modeling: Modeling, rules: Rules);

  /**
   * Align elements on relevant axis for given alignment.
   *
   * @param elements
   * @param type
   */
  trigger(elements: Element[], type: Alignment): void;
}

type Element = import('diagram-js/lib/model/Types').Element;
type Modeling = import('diagram-js/lib/features/modeling/Modeling').default;
type Rules = import('diagram-js/lib/features/rules/Rules').default;
type Axis = import('diagram-js/lib/util/Types').Axis;
type Dimension = import('diagram-js/lib/util/Types').Dimension;
export type Alignment = 'top' | 'right' | 'bottom' | 'left' | 'center' | 'middle';
