/**
 * A service that places elements connected to existing ones
 * to an appropriate position in an _automated_ fashion.
 *
 */
export default class AutoPlace {
    static $inject: string[];
    /**
     * @param eventBus
     * @param modeling
     * @param canvas
     */
    constructor(eventBus: EventBus, modeling: Modeling, canvas: Canvas);
    /**
     * Append shape to source at appropriate position.
     *
     * @param source
     * @param shape
     * @param hints
     *
     * @return appended shape
     */
    append: (source: Shape, shape: Shape, hints?: any) => Shape;
}

type Shape = import('diagram-js/lib/core/Types').ShapeLike;
type Point = import('diagram-js/lib/util/Types').Point;
type Canvas = import('diagram-js/lib/core/Canvas').default;
type EventBus = import('diagram-js/lib/core/EventBus').default;
type Modeling = import('diagram-js/lib/features/modeling/Modeling').default;
