/**
 * A component that implements moving of bendpoints.
 *
 */
export default class ConnectionSegmentMove {
    static $inject: string[];
    /**
     * @param injector
     * @param eventBus
     * @param canvas
     * @param dragging
     * @param graphicsFactory
     * @param modeling
     */
    constructor(injector: Injector, eventBus: EventBus, canvas: Canvas, dragging: Canvas, graphicsFactory: GraphicsFactory, modeling: Modeling);
    start: (event: any, connection: any, idx: any) => void;
}

type Shape = import('diagram-js/lib/model/Types').Shape;
type Axis = import('diagram-js/lib/util/Types').Axis;
type Point = import('diagram-js/lib/util/Types').Point;
type Injector = import('didi').Injector;
type Canvas = import('diagram-js/lib/core/Canvas').default;
type Dragging = import('diagram-js/lib/features/dragging/Dragging').default;
type EventBus = import('diagram-js/lib/core/EventBus').default;
type GraphicsFactory = import('diagram-js/lib/core/GraphicsFactory').default;
type Modeling = import('diagram-js/lib/features/modeling/Modeling').default;
