export function isReverse(context: any): boolean;

export default class Connect {
    static $inject: string[];
    /**
     * @param eventBus
     * @param dragging
     * @param modeling
     * @param rules
     */
    constructor(eventBus: EventBus, dragging: Dragging, modeling: Modeling, rules: Rules);
    /**
     * Start connect operation.
     *
     * @param event
     * @param start
     * @param connectionStart
     * @param autoActivate
     */
    start: (event: MouseEvent | TouchEvent, start: Element, connectionStart?: Point, autoActivate?: boolean) => void;
}

type Element = import('diagram-js/lib/model/Types').Element;
type Point = import('diagram-js/lib/util/Types').Point;
type Dragging = import('diagram-js/lib/features/dragging/Dragging').default;
type EventBus = import('diagram-js/lib/core/EventBus').default;
type Modeling = import('diagram-js/lib/features/modeling/Modeling').default;
type Rules = import('diagram-js/lib/features/rules/Rules').default;
