/**
 * Groups and filters elements and then trigger even distribution.
 *
 */
export default class DistributeElements {
  static $inject: string[];

  /**
   * @param modeling
   * @param rules
   */
  constructor(modeling: Modeling, rules: Rules);

  /**
   * Registers filter functions that allow external parties to filter
   * out certain elements.
   *
   * @param filterFn
   */
  registerFilter(filterFn: (distributableElements: Element[], axis: Axis, dimension: Dimension) => Element[]): void;

  /**
   * Distributes the elements with a given orientation
   *
   * @param elements
   * @param orientation
   */
  trigger(elements: Element[], orientation: string): Group[];
}

type Element = import('diagram-js/lib/model/Types').Element;
type Axis = import('diagram-js/lib/util/Types').Axis;
type Dimension = import('diagram-js/lib/util/Types').Dimension;
type Rect = import('diagram-js/lib/util/Types').Rect;
type Modeling = import('diagram-js/lib/features/modeling/Modeling').default;
type Rules = import('diagram-js/lib/features/rules/Rules').default;

export type Range = {
    min: number;
    max: number;
};

export type Group = {
    elements: Element[];
    range: Range;
};
