export default class GlobalConnect {
  static $inject: string[];

  /**
   * @param eventBus
   * @param dragging
   * @param connect
   * @param canvas
   * @param toolManager
   * @param rules
   * @param mouse
   */
  constructor(eventBus: EventBus, dragging: Dragging, connect: Connect, canvas: Canvas, toolManager: ToolManager, rules: Rules, mouse: Mouse);

  /**
   * Initiates tool activity.
   */
  start(event: any, autoActivate: any): void;

  toggle(): void;
  isActive(): boolean;

  /**
   * Check if source element can initiate connection.
   *
   * @param startTarget
   * @return
   */
  canStartConnect(startTarget: Element): boolean;
}

type Canvas = import('diagram-js/lib/core/Canvas').default;
type Connect = import('diagram-js/lib/features/connect/Connect').default;
type Dragging = import('diagram-js/lib/features/dragging/Dragging').default;
type EventBus = import('diagram-js/lib/core/EventBus').default;
type Mouse = import('diagram-js/lib/features/mouse/Mouse').default;
type Rules = import('diagram-js/lib/features/rules/Rules').default;
type ToolManager = import('diagram-js/lib/features/tool-manager/ToolManager').default;
type Element = import('diagram-js/lib/model/Types').Element;
