export default class LassoTool {
  static $inject: string[];

  /**
   * @param eventBus
   * @param canvas
   * @param dragging
   * @param elementRegistry
   * @param selection
   * @param toolManager
   * @param mouse
   */
  constructor(eventBus: EventBus, canvas: Canvas, dragging: Dragging, elementRegistry: ElementRegistry, selection: Selection, toolManager: ToolManager, mouse: Mouse);

  /**
   * Activate lasso.
   *
   * @param event
   * @param autoActivate
   */
  activateLasso(event: MouseEvent, autoActivate?: boolean): void;

  /**
   * Activate selection.
   *
   * @param event
   * @param autoActivate
   */
  activateSelection(event: MouseEvent, autoActivate?: boolean): void;

  /**
   * Select elements within the given bounds.
   *
   * @param elements
   * @param bbox
   * @param previousSelection
   */
  select(elements: Element[], bbox: Rect, previousSelection?: Element[]): void;

  /**
   * Toggle the lasso tool.
   */
  toggle(): void;

  /**
   * Check if the lasso tool is active.
   *
   * @returns
   */
  isActive(): boolean;
}

type Canvas = import('diagram-js/lib/core/Canvas').default;
type Dragging = import('diagram-js/lib/features/dragging/Dragging').default;
type ElementRegistry = import('diagram-js/lib/core/ElementRegistry').default;
type EventBus = import('diagram-js/lib/core/EventBus').default;
type Mouse = import('diagram-js/lib/features/mouse/Mouse').default;
type Selection = import('diagram-js/lib/features/selection/Selection').default;
type ToolManager = import('diagram-js/lib/features/tool-manager/ToolManager').default;
type Rect = import('diagram-js/lib/util/Types').Rect;
