/**
 * A handler that implements reversible appending of shapes
 * to a source shape.
 *
 */
export default class AppendShapeHandler {
  static $inject: string[];

  /**
   * @param modeling
   */
  constructor(modeling: Modeling);

  /**
   * Creates a new shape.
   *
   * @param context
   */
  preExecute(context: {
      shape: Partial<Shape>;
      source: Element;
      parent: Parent;
      position: Point;
  }): void;

  postExecute(context: any): void;
}

type Element = import('diagram-js/lib/model/Types').Element;
type Parent = import('diagram-js/lib/model/Types').Parent;
type Shape = import('diagram-js/lib/model/Types').Shape;
type Point = import('diagram-js/lib/util/Types').Point;
type Modeling = import('diagram-js/lib/features/modeling/Modeling').default;
