export default class CreateConnectionHandler {
  static $inject: string[];

  /**
   * @param canvas
   * @param layouter
   */
  constructor(canvas: Canvas, layouter: Layouter);

  /**
   * Creates a new connection between two elements.
   *
   * @param context
   * connection to the parent's children.
   */
  execute(context: {
      source: Element;
      target: Element;
      parent: Shape;
      parentIndex?: number;
      hints?: ModelingHints;
  }): any;

  revert(context: any): any;
}

type Element = import('diagram-js/lib/model/Types').Element;
type Shape = import('diagram-js/lib/model/Types').Shape;
type Point = import('diagram-js/lib/util/Types').Point;
type ModelingHints = import('diagram-js/lib/features/modeling/Modeling').ModelingHints;
type Canvas = import('diagram-js/lib/core/Canvas').default;
type Layouter = import('diagram-js/lib/layout/BaseLayouter').default;
