/**
 * A handler that attaches a label to a given target shape.
 *
 */
export default class CreateLabelHandler extends CreateShapeHandler {
    /**
     * Append label to element.
     *
     * @param context
     */
    execute(context: {
        parent: Parent;
        position: Point;
        shape: Shape;
        target: Element;
    }): any;
}

type Canvas = import('diagram-js/lib/core/Canvas').default;
type Element = import('diagram-js/lib/model/Types').Element;
type Parent = import('diagram-js/lib/model/Types').Parent;
type Shape = import('diagram-js/lib/model/Types').Shape;
type Point = import('diagram-js/lib/util/Types').Point;
import CreateShapeHandler from 'diagram-js/lib/features/modeling/cmd/CreateShapeHandler';
