/**
 * A plugin that makes shapes draggable / droppable.
 *
 */
export default class MoveEvents {
    static $inject: string[];
    /**
     * @param eventBus
     * @param dragging
     * @param modeling
     * @param selection
     * @param rules
     */
    constructor(eventBus: EventBus, dragging: Dragging, modeling: Modeling, selection: Selection, rules: Rules);
    start: (event: MouseEvent | TouchEvent, element: Shape, activate?: boolean, context?: any) => boolean;
}

type Element = import('diagram-js/lib/core/Types').ElementLike;
type Shape = import('diagram-js/lib/core/Types').ShapeLike;
type Dragging = import('diagram-js/lib/features/dragging/Dragging').default;
type EventBus = import('diagram-js/lib/core/EventBus').default;
type Modeling = import('diagram-js/lib/features/modeling/Modeling').default;
type Rules = import('diagram-js/lib/features/rules/Rules').default;
type Selection = import('diagram-js/lib/features/selection/Selection').default;
