/**
 * Provides previews for moving shapes when moving.
 *
 */
export default class MovePreview {
    static $inject: string[];
    /**
     * @param eventBus
     * @param canvas
     * @param styles
     * @param previewSupport
     */
    constructor(eventBus: EventBus, canvas: Canvas, styles: Styles, previewSupport: PreviewSupport);
    /**
     * Make an element draggable.
     *
     * @param context
     * @param element
     * @param addMarker
     */
    makeDraggable: (context: any, element: Element, addMarker: boolean) => void;
}

type Element = import('diagram-js/lib/model/Types').Element;
type Canvas = import('diagram-js/lib/core/Canvas').default;
type EventBus = import('diagram-js/lib/core/EventBus').default;
type PreviewSupport = import('diagram-js/lib/features/preview-support/PreviewSupport').default;
type Styles = import('diagram-js/lib/draw/Styles').default;
