/**
 * A component that renders the popup menus.
 *
 */
export default class PopupMenuComponent {
    /**
     * @param props
     */
    constructor(props: {
        onClose: () => void;
        onSelect: () => void;
        position: (element: HTMLElement) => Point;
        className: string;
        entries: PopupMenuEntry[];
        headerEntries: PopupMenuHeaderEntry[];
        scale: number;
        title?: string;
        search?: boolean;
        emptyPlaceholder?: PopupMenuEmptyPlaceholder;
        width?: number;
    });
}

type PopupMenuEntry = import('diagram-js/lib/features/popup-menu/PopupMenuProvider').PopupMenuEntry;
type PopupMenuHeaderEntry = import('diagram-js/lib/features/popup-menu/PopupMenuProvider').PopupMenuHeaderEntry;
export type PopupMenuEmptyPlaceholder = import('diagram-js/lib/features/popup-menu/PopupMenuProvider').PopupMenuEmptyPlaceholderProvider | import('diagram-js/lib/features/popup-menu/PopupMenuProvider').PopupMenuEmptyPlaceholder;
type Point = import('diagram-js/lib/util/Types').Point;
